```java
// Different sorting strategies for different contexts

// Factory method to get appropriate sorting strategy
public Comparator<Product> getContextAwareComparator(BrowsingContext context) {
    // User is searching
    if (context.isSearchMode() && context.getSearchTerm() != null) {
        return Comparator.comparing(product -> 
            -calculateSearchRelevance(product, context.getSearchTerm()));
    }

    // User is browsing a category
    if (context.getCategory() != null) {
        // Different sorting for different categories
        switch (context.getCategory()) {
            case ELECTRONICS:
                // For electronics, sort by newness then rating
                return Comparator.comparing(Product::getReleaseDate, 
                           Comparator.nullsLast(Comparator.reverseOrder()))
                         .thenComparing(Product::getRating, Comparator.reverseOrder());

            case CLOTHING:
                // For clothing, prioritize items with full size range
                return Comparator.comparing(
                    product -> -((ClothingProduct)product).getAvailableSizes().size())
                         .thenComparing(Product::getRating, Comparator.reverseOrder());

            case GROCERY:
                // For grocery, fresh items first
                return Comparator.comparing(Product::getAddedDate, Comparator.reverseOrder());

            default:
                // Default product sorting
                return Comparator.comparing(Product::getPopularityScore, 
                           Comparator.reverseOrder());
        }
    }

    // Homepage or general browsing: personalized sort
    if (context.getUserPreferences() != null) {
        return personalizedComparator(context.getUserPreferences());
    }

    // Default fallback sorting
    return Comparator.comparing(Product::getPopularityScore, Comparator.reverseOrder())
                   .thenComparing(Product::getPrice);
}

// Usage
products.sort(getContextAwareComparator(currentBrowsingContext));
