// Using embedded database for complex state
public class DatabaseStateManager {
    private Connection connection;

    public DatabaseStateManager() {
        try {
            connection = DriverManager.getConnection("jdbc:h2:~/appstate");
            createTablesIfNeeded();
        } catch (SQLException e) {
            logger.error("Database initialization failed", e);
        }
    }

    public void savePreference(String key, String value) {
        try (PreparedStatement stmt = connection.prepareStatement(
                "MERGE INTO preferences (key, value) VALUES (?, ?)")) {
            stmt.setString(1, key);
            stmt.setString(2, value);
            stmt.executeUpdate();
        } catch (SQLException e) {
            logger.error("Failed to save preference", e);
        }
    }

    public String getPreference(String key, String defaultValue) {
        try (PreparedStatement stmt = connection.prepareStatement(
                "SELECT value FROM preferences WHERE key = ?")) {
            stmt.setString(1, key);
            ResultSet rs = stmt.executeQuery();
            if (rs.next()) {
                return rs.getString("value");
            }
        } catch (SQLException e) {
            logger.error("Failed to get preference", e);
        }
        return defaultValue;
    }
}
